void CalcTimes_C(float &T1, float &T2, uint16_t &Ta, uint16_t &Tb, uint16_t &Tc)
{
	T1 *= TIM1->ARR;
	T2 *= TIM1->ARR;
	Ta = (TIM1->ARR-T1-T2)/2;
	Tb = Ta + T1;
	Tc = Tb + T2;
}

#define CalcTimes(x) CalcTimes_C(T1, T2, Ta, Tb, Tc)

float T1, T2;
int sector = 0;
uint16_t Ta, Tb, Tc;
int apply_power(float Vr1, float Vr2, float Vr3)
{
	uint16_t period = TIM1->ARR;
	uint16_t dPWM1 = 0, dPWM2 = 0, dPWM3 = 0;
	
	if( Vr1 >= 0 )
	{
		// (xx1)
		if( Vr2 >= 0 )
		{
			// (x11)
			// Must be Sector 3 since Sector 7 not allowed
			// Sector 3: (0,1,1)  0-60 degrees
			T1 = Vr2;
			T2 = Vr1;
			CalcTimes();
			dPWM1 = Ta;
			dPWM2 = Tb;
			dPWM3 = Tc;
			sector = 0;
		}
		else
		{
			// (x01)
			if( Vr3 >= 0 )
			{
				// Sector 5: (1,0,1)  120-180 degrees
				T1 = Vr1;
				T2 = Vr3;
				CalcTimes();
				dPWM1 = Tc;
				dPWM2 = Ta;
				dPWM3 = Tb;
				sector = 1;
			}
			else
			{
				// Sector 1: (0,0,1)  60-120 degrees
				T1 = -Vr2;
				T2 = -Vr3;
				CalcTimes();
				dPWM1 = Tb;
				dPWM2 = Ta;
				dPWM3 = Tc;
				sector = 2;
			}
		}
	}
	else
	{
		// (xx0)
		if( Vr2 >= 0 )
		{
			// (x10)
			if( Vr3 >= 0 )
			{
				// Sector 6: (1,1,0)  240-300 degrees
				T1 = Vr3;
				T2 = Vr2;
				CalcTimes();
				dPWM1 = Tb;
				dPWM2 = Tc;
				dPWM3 = Ta;
				sector = 3;
			}
			else
			{
				// Sector 2: (0,1,0)  300-0 degrees
				T1 = -Vr3;
				T2 = -Vr1;
				CalcTimes();
				dPWM1 = Ta;
				dPWM2 = Tc;
				dPWM3 = Tb;
				sector = 4;
			}
		}
		else
		{            
			// (x00)
			// Must be Sector 4 since Sector 0 not allowed
			// Sector 4: (1,0,0)  180-240 degrees
			T1 = -Vr1;
			T2 = -Vr2;
			CalcTimes();
			dPWM1 = Tc;
			dPWM2 = Tb;
			dPWM3 = Ta;
			sector = 5;
		}
	}
	
	TIM1->CCR1 = dPWM1;
	TIM1->CCR2 = dPWM2;
	TIM1->CCR3 = dPWM3;

	return 0;
}

